import React, { Component } from 'react'
import '../style/Home.css'
import axios from "axios";
import '../style/Spinner.css'
// import { isMobile } from 'react-device-detect'
import NotifyError from './NotifyError'

class Home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            pesquisar: false,
            loading: false,
            textoPesquisa: '',
            filme: {}
        }
        this.pesquisar = this.pesquisar.bind(this)
    }

    sair () {
        this.props.onSair()
    }

    pesquisar (val) {
        this.setState({textoPesquisa:val})
        if (val && val.length > 3) {
            this.setState({loading:true})
            const campoPesquisa = val.replace(' ', '+')
            axios.post('https://www.omdbapi.com/?t=' + campoPesquisa + '&apikey=efbb6c79')
                .then((response) => {
                    this.setState({filme:response.data})
                }).catch((error) => {
                this.refs.NotifyError.abrir(error.message)
            }).finally(() => {
                this.setState({loading:false})
            })
        } else {
            this.setState({filme: {}})
        }
    }

    render () {
        return (
            <div>
                <NotifyError ref="NotifyError"/>
                <div className="Menu">
                    <ul id="Ul">
                        {/*<li id="small-logo"></li>*/}
                        <li id="Li"> FILMES </li>
                        <li id="Li"> SÃ‰RIES </li>
                        <li id="Li" onClick={event => this.sair()}> SAIR</li>
                        <li id="icon1">
                            {/*<img className="icon1" src="./Angular_files/twitter-logo.png" alt="twitter-logo" />*/}
                        </li>
                        <li id="icon2">
                            {/*<img className="icon2" src="./Angular_files/github-logo.png" alt="github-logo" />*/}
                        </li>
                    </ul>
                </div>
                <div className="Div-principal">
                    {/*elemento central*/}
                </div>
            </div>
        );
    }
};

export default Home;
