import React, { Component } from 'react'
import '../style/Home.css'
import logo from '../assets/logo.svg'
import MyInput from './MyInput'
import axios from "axios";
import debounce from 'lodash.debounce';

class Home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            pesquisar: true,
            loading: false,
            textoPesquisa: '',
            filme: {
                Title: Teste
            }
        }
        this.pesquisar = this.pesquisar.bind(this)
    }

    pesquisar (val) {
        this.setState({textoPesquisa:val})
        if (val && val.length > 3) {
            setTimeout(() => {
                const campoPesquisa = val.replace(' ', '+')
                axios.post('http://www.omdbapi.com/?t=' + campoPesquisa + '&apikey=efbb6c79')
                    .then((response) => {
                        this.setState({filme:response.data})
                        console.log('filme: ', this.state.filme)
                    }).catch((error) => {
                    alert(error.message)
                })
            }, 800)
        }
    }

    render () {
        let elementoCentral
        let cardFilme

        if (this.state.loading) {
            cardFilme = <div>loading</div>
        } else if (this.state.filme && this.state.filme.Title) {
            cardFilme = <div className="Filme">filme</div>
        } else if (this.state.textoPesquisa) {
            cardFilme = <div>Nenhum resultado encontrado</div>
        } else {
            cardFilme = <div>
            </div>
        }

        if (this.state.pesquisar) {
            elementoCentral =
                <div>
                    <div className="Wrapper">
                        <div className="Mid-pesquisa">
                            <input onChange={event => { this.pesquisar(event.target.value) }} className="Pesquisa-principal" placeholder={'Pesquisar'}/>
                            {cardFilme}
                        </div>
                    </div>
                </div>
        } else {
            elementoCentral = <div>
                <img className="Logo-maior" src={logo} alt="MyMovies.logo" />
                <div className="Fundo-logo">
                </div>
                <div className="Wrapper">
                    <div className="Mid-container">
                        <div className="Text">
                            Tudo Sobre Filmes.
                            <br />
                            Em um só Lugar.
                            <br />
                        </div>
                        {/*href="http://wwatana.be/mobile/projetos-2019/02/index.html"*/}
                        <a className="Button">Começar</a>
                    </div>
                </div>
            </div>
        }

        return (
            <div>
                <div className="Menu">
                    <ul id="Ul">
                        {/*<li id="small-logo"></li>*/}
                        <li id="Li"> FILMES </li>
                        <li id="Li"> DIRETORES </li>
                        <li id="Li"> ATORES </li>
                        <li id="Li"> SÉRIES </li>
                        <li id="Li"> CURIOSIDADES</li>
                        <li id="Li"> SAIR</li>
                        {/*<li id="Busca">
                            <div className="divBusca">
                                <input type="text" id="TxtBusca" placeholder="Pesquisar" />
                            </div>
                        </li>*/}
                        <li id="icon1">
                            {/*<img className="icon1" src="./Angular_files/twitter-logo.png" alt="twitter-logo" />*/}
                        </li>
                        <li id="icon2">
                            {/*<img className="icon2" src="./Angular_files/github-logo.png" alt="github-logo" />*/}
                        </li>
                    </ul>
                </div>
                <div className="Div-principal">
                    {elementoCentral}
                </div>
            </div>
        );
    }
};

export default Home;
