import React, { Component } from 'react'
import '../style/NotifyError.css'

class NotifyError extends Component {
    constructor(props) {
        super(props);

        this.state = {
            modalAberto: true
        }
    }
    render () {
        if (this.state.modalAberto) {
            return (
                <div className="Modal">
                    <div className="Card">
                        <button className="Fechar">X</button>
                        <div className="Texto">{this.props.texto}</div>
                    </div>
                </div>
            )

        } else {
            return null
        }
    }
}

export default NotifyError;
