import React, { Component } from 'react';
import './style/App.css';
import TelaInicial from "./components/TelaInicial";
import CadastrosConteudo from "./components/CadastrosConteudo";
import Home from './components/Home'

class App extends Component{
    constructor(props) {
        super(props);

        this.state = {
            usuario: {},
            rota: 'TelaInicial',
            token: ''
        }
        this.entrou = this.entrou.bind(this)
        this.saiu = this.saiu.bind(this)
        this.state.token = localStorage.getItem('token')
        this.state.usuario = localStorage.getItem('usuario')
    }

    changeRota (val) {
        this.setState({rota:val})
    }

    entrou (val) {
        localStorage.setItem('token', val.token)
        localStorage.setItem('usuario', val)
        this.setState({usuario:val})
        if (val.adm) {
            this.changeRota('CadastrosConteudo')
        } else {
            this.changeRota('Home')
        }
    }

    saiu (val) {
        this.setState({token:''})
        localStorage.setItem('token', '')
        this.changeRota('TelaInicial')
    }

    render() {
        if (this.state.rota === 'Home' || (this.state.token && this.state.usuario && !this.state.usuario.adm)) {
            return (
                <Home onSair={(event) => this.saiu()}/>
            );
        } else if (this.state.rota === 'CadastrosConteudo' || (this.state.token && this.state.usuario && this.state.usuario.adm)) {
            return (
                <CadastrosConteudo/>
            );
        } else if (this.state.rota === 'TelaInicial') {
            return (
                <TelaInicial onEntrou={(event) => this.entrou(event)}/>
            );
        }
    }
}

export default App;
