import React, { Component } from 'react';
import './style/App.css';
import TelaInicial from "./components/TelaInicial";
import Home from './components/Home'

class App extends Component{
    constructor(props) {
        super(props);

        this.state = {
            rota: 'TelaInicial',
            usuario: {}
        }
        this.entrou = this.entrou.bind(this)
        this.saiu = this.saiu.bind(this)
        this.setState({usuario:localStorage.getItem('usuario')})
    }

    changeRota (val) {
        this.setState({rota:val})
    }

    entrou (val) {
        localStorage.setItem('usuario', val)
        this.setState({usuario:val})
        this.changeRota('Home')
    }

    saiu (val) {
        this.setState({usuario:{}})
        this.changeRota('TelaInicial')
    }

    render() {
        console.log('this.state.usuario.token: ', this.state.usuario)
        if (this.state.rota === 'Home' || (this.state.usuario && this.state.usuario.token)) {
            return (
                <Home onSair={(event) => this.saiu()}/>
            );
        } else if (this.state.rota === 'TelaInicial') {
            return (
                <TelaInicial onEntrou={(event) => this.entrou(event)}/>
            );
        }
    }
}

export default App;
