import React, { Component } from 'react'
import '../style/Home.css'
import logo from '../assets/logo.svg'

class Home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            pesquisar: true
        }
    }

    render () {
        let elementoCentral
        if (this.state.pesquisar) {
            elementoCentral = <div>pesquisar</div>
        } else {
            elementoCentral = <div>
                <img className="Logo-maior" src={logo} alt="MyMovies.logo" />
                <div className="Fundo-logo">
                </div>
                <div className="Wrapper">
                    <div className="Mid-container">
                        <div className="Text">
                            Tudo Sobre Filmes.
                            <br />
                            Em um só Lugar.
                            <br />
                        </div>
                        {/*href="http://wwatana.be/mobile/projetos-2019/02/index.html"*/}
                        <a className="Button">Começar</a>
                    </div>
                </div>
            </div>
        }
        return (
            <div>
                <div className="Menu">
                    <ul id="Ul">
                        {/*<li id="small-logo"></li>*/}
                        <li id="Li"> FILMES </li>
                        <li id="Li"> DIRETORES </li>
                        <li id="Li"> ATORES </li>
                        <li id="Li"> SÉRIES </li>
                        <li id="Li"> CURIOSIDADES</li>
                        <li id="Busca">
                            <div className="divBusca">
                                <input type="text" id="TxtBusca" placeholder="Pesquisar" />
                            </div>
                        </li>
                        <li id="icon1">
                            {/*<img className="icon1" src="./Angular_files/twitter-logo.png" alt="twitter-logo" />*/}
                        </li>
                        <li id="icon2">
                            {/*<img className="icon2" src="./Angular_files/github-logo.png" alt="github-logo" />*/}
                        </li>
                    </ul>
                </div>
                <div className="Div-principal">
                    {elementoCentral}
                </div>
            </div>
        );
    }
};

export default Home;
