        super(props);

        this.state = {
            login: ''
        }
        this.entrar = this.entrar.bind(this);
    }
    entrar () {
        console.log('entrou')
        this.props.onEntrar(this.state.usuario);
    }
    render () {
        return (
            /*onChange={event => onChange(changeValue('Nome', event.target.value))}*/
            <div className="Login-card">
                <span className="Cadastrar-area">{this.state.login}</span>
                <span className="Title">Entrar</span>
