import React, { Component } from 'react';
import MyInput from './MyInput'
import '../style/Login.css'

class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            login: '',
            senha: ''
        }
        this.entrar = this.entrar.bind(this);
    }
    entrar () {
        if (this.state.login && this.state.senha) {
            this.props.onEntrar({ login: this.state.login, senha: this.state.senha });
        } else {
            alert('teste')
        }
    }
    render () {
        return (
            /*onChange={event => onChange(changeValue('Nome', event.target.value))}*/
            <div className="Login-card">
                <span className="Title">Entrar</span>
                <MyInput onChange={(event) => { this.setState({login:event.target.value}) }} label={'Nome'} value={this.state.login}/>
                <MyInput onChange={(event) => { this.setState({senha:event.target.value}) }} label={'Senha'} value={this.state.senha}/>
                <button onClick={(event) => this.entrar()} className="Botao-entrar">Entrar</button>
                <div className="Cadastrar-area"><span className="Cadastrar-pergunta">NÃ£o tem uma conta?</span><span onClick={this.props.onCadastrar} className="Cadastrar-se">Cadastre-se</span></div>
            </div>
        )
}
}

export default Login;
